mapper.select:
SELECT count(*)
FROM user_master
WHERE del_flg = 0 AND user_name = #{username}

mapper.update:
        UPDATE
            invitation_management

        SET
            guest_user_id = #{rec.guestuserid},
            used_flg = 1,
            update_user_id = #{rec.updateuserid},
            update_date = #{rec.updatedate}

        WHERE
            invitation_code = #{rec.invitationcode}
        AND guest_user_id IS NULL
        AND guest_telephone_number = #{rec.guesttelephonenumber}
        AND expiration_date  >=  #{rec.expirationdate}
        AND used_flg = 0
